import React, { useState } from "react";
import "../styles/Login.css";
import Logo from "../assets/logo light.jpg";

function Login() {
  const [showCard2, setShowCard2] = useState(false);

  const handleLoginClick = () => {
    setShowCard2(true);
  };

  return (
    <div className="Login-Cards">
      <div
        className={`card-transition ${showCard2 ? "card-hide" : "card-show"}`}
      >
        <div className="Login-card-1">
          <img src={Logo} alt="" />
          <h2>Login to Medical Notes</h2>
          <p>Access patient data, prescriptions, and visit history securely.</p>
          <button className="btn btn-primary" onClick={handleLoginClick}>
            Login
          </button>
        </div>
      </div>

      <div
        className={`card-transition ${showCard2 ? "card-show" : "card-hide"}`}
      >
        <div class="login-container Login-card-2">
          <img src="Assects/logo light.jpg" alt="MedicNotes" class="logo" />
          <h2>Sign in to MedicNotes</h2>
          {/* <p class="subtitle">Access your Account securely</p> */}
          <label for="role">I am a</label>
          <select id="role">
            <option value="patient">Patient</option>
            <option value="doctor">Doctor</option>
            <option value="admin">Admin</option>
          </select>
          <label for="email">Username or Email or Id</label>
          <input
            type="text"
            id="email"
            placeholder="e.g., dr.john@example.com"
          />
          <label for="password">Password</label>
          <input type="password" id="password" placeholder="password" />
          <button class="btn">Next</button>
        </div>
      </div>
    </div>
  );
}

export default Login;
