import React, { useState } from "react";
import "../styles/Login.css";
import Logo from "../assets/logo light.jpg";
import LoginBackground from "../assets/medicnotes-hero.webp";

function Login({ setAuth }) {
  const [showCard2, setShowCard2] = useState(false);
  const [role, setRole] = useState("patient");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleLoginClick = () => {
    setShowCard2(true);
    setErrorMessage("");
  };

  const handleNextClick = async () => {
    let url = "";
    let body = {};

    if (role === "admin") {
      url = "http://localhost:8080/auth/admin/login";
      body = {
        adminId: username,
        password: password,
      };
    } else if (role === "doctor") {
      url = "http://localhost:8080/auth/doctor/login";
      body = {
        email: username,
        password: password,
      };
    } else if (role === "patient") {
      url = "http://localhost:8080/auth/patient/login";
      body = {
        phone: username,
        password: password,
      };
    }

    try {
      const response = await fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(body),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error("Server responded with error:", errorData);
        const message =
          errorData.message || "Server error. Please try again later.";
        throw new Error(message);
      }

      const data = await response.json();

      localStorage.setItem("token", data.token);
      localStorage.setItem("role", role);

      console.log("Login successful. Token:", data.token);
      console.log("Role set as:", role);

      setAuth({
        isAuthenticated: true,
        role: role.toUpperCase(), // Convert to "ADMIN", "DOCTOR", "PATIENT"
        username: data.name || username, // Use backend name or input if not returned
      });
    } catch (error) {
      console.error("Login error:", error);
      setErrorMessage(error.message);
    }
  };

  return (
