        <header>
          <div class="navbar">
            <div class="logo">
              <span>MedicNotes</span>
            </div>
            <button class="contact-btn">Contact Us</button>
          </div>
        </header>

        <section class="hero">
          <div class="hero-content">
            <h1>
              MedicNotes:
              <br />
              Empowering Doctors
            </h1>
            <p>
              Streamline your workflow with intuitive, AI-powered medical
              documentation.
            </p>
            <button class="discover-btn">Login</button>
          </div>
          <div class="hero-bg"></div>
        </section>
      </div>
    </div>
  );
}

