import React, { useState } from "react";
import "../styles/Login.css";
import Logo from "../assets/logo light.jpg";

function Login({ setAuth }) {
  const [showCard2, setShowCard2] = useState(false);
  const [role, setRole] = useState("patient");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleLoginClick = () => {
    setShowCard2(true);
    setErrorMessage("");
  };

  const handleNextClick = async () => {
    let url = "";
    let body = {};

    if (role === "admin") {
      url = "http://localhost:8080/auth/admin/login";
      body = { adminId: username, password: password };
    } else if (role === "doctor") {
      url = "http://localhost:8080/auth/doctor/login";
      body = { email: username, password: password };
    } else if (role === "patient") {
      url = "http://localhost:8080/auth/patient/login";
      body = { phone: username, password: password };
    }

    try {
      const response = await fetch(url, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Server error. Please try again.");
      }

      const data = await response.json();
      localStorage.setItem("token", data.token);
      localStorage.setItem("role", role);

      setAuth({
        isAuthenticated: true,
        role: role.toUpperCase(),
        username: data.name || username,
      });
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  return (
    <div className="Login-Cards">
      {!showCard2 && (
        <div className="MainPage">
          <div className="shade">
            <header>
              <div className="navbar">
                <div className="logo">
                  <span>MedicNotes</span>
                </div>
                <button className="contact-btn">Contact Us</button>
              </div>
            </header>

            <section className="hero">
              <div className="hero-content">
                <h1>
                  MedicNotes:
                  <br />
                  Empowering Doctors
                </h1>
                <p>
                  Streamline your workflow with intuitive, AI-powered medical
                  documentation.
                </p>
                <button className="discover-btn" onClick={handleLoginClick}>
                  Login
                </button>
              </div>
              <div className="hero-bg"></div>
            </section>
          </div>
        </div>
      )}

      {showCard2 && (
        <div className="card-transition card-show">
          <div className="login-container Login-card-2">
            <img src={Logo} alt="" className="logo" />
            <h2>Sign in to MedicNotes</h2>
            <p className="subtitle">Access your Account securely</p>

            <label htmlFor="role">I am a</label>
            <select
              id="role"
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="patient">Patient</option>
              <option value="doctor">Doctor</option>
              <option value="admin">Admin</option>
            </select>

            <label htmlFor="email">Username / Email / ID</label>
            <input
              type="text"
              id="email"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="e.g., dr.john@example.com"
            />

            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="password"
            />

            {errorMessage && (
              <div className="error-message">{errorMessage}</div>
            )}

            <button className="btn" onClick={handleNextClick}>
              Next
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Login;
