import React, { useState, useEffect } from "react";
import "../styles/Login.css";

function Login({ setAuth }) {
  const [showCard2, setShowCard2] = useState(false);
  const [role, setRole] = useState("patient");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  // ✅ Listen to browser back button
  useEffect(() => {
    const handlePopState = () => {
      setShowCard2(false);
    };

    window.addEventListener("popstate", handlePopState);

    return () => {
      window.removeEventListener("popstate", handlePopState);
    };
  }, []);

  const handleLoginClick = () => {
    setShowCard2(true);
    setErrorMessage("");

    // ✅ Push new state so browser back works
    window.history.pushState({ page: "login" }, "", "#login");
  };

  const handleBackClick = () => {
    setShowCard2(false);
    window.history.back(); // ✅ Go back in history
  };

  const handleNextClick = async () => {
    let url = "";
    let body = {};

    if (role === "admin") {
      url = "http://localhost:8080/auth/admin/login";
      body = { adminId: username, password: password };
    } else if (role === "doctor") {
      url = "http://localhost:8080/auth/doctor/login";
      body = { email: username, password: password };
    } else if (role === "patient") {
      url = "http://localhost:8080/auth/patient/login";
      body = { phone: username, password: password };
    }

    try {
      const response = await fetch(url, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Server error. Please try again.");
      }

      const data = await response.json();
      localStorage.setItem("token", data.token);
      localStorage.setItem("role", role);

      setAuth({
        isAuthenticated: true,
        role: role.toUpperCase(),
        username: data.name || username,
      });
    } catch (error) {
      setErrorMessage(error.message);
      console.error("Login failed:", error.message);
    }
  };

  return (
    <div className="Login-Cards">
      <div className="MainPage">
        <div className="shade">
          <header>
            <div className="navbar">
              <div className="logo">
                <span>MedicNotes</span>
              </div>
              <button className="contact-btn">Contact Us</button>
            </div>
          </header>

          <section className={`hero ${showCard2 ? "center-content" : ""}`}>
            {!showCard2 && (
              <div className="hero-content">
                <h1>
                  MedicNotes:
                  <br />
                  Empowering Doctors
                </h1>
                <p>
                  Streamline your workflow with intuitive, AI-powered medical
                  documentation.
                </p>
                <button className="discover-btn" onClick={handleLoginClick}>
                  Login
                </button>
              </div>
            )}

            {showCard2 && (
              // <div className="card-transition card-show">
              //   <div className="login-container Login-card-2">
              //     <button className="back-arrow" onClick={handleBackClick}>
              //       &larr;
              //     </button>
              //     <h2>Sign in to MedicNotes</h2>
              //     <p className="subtitle">Access your Account securely</p>

              //     <label htmlFor="role">I am a</label>
              //     <select
              //       id="role"
