import React, { useState, useEffect, useRef } from "react";
import "../styles/AdminDashboard.css";

// const AdminDashboard = () => {
function AdminDashboard() {
  const [theme, setTheme] = useState(localStorage.getItem("theme") || "dark");
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const searchInputRef = useRef(null);

  useEffect(() => {
    document.body.setAttribute("data-theme", theme);

    function updateThemeIcon(theme) {
      if (theme === "dark") {
        // Assuming you have fontawesome icons
        document.getElementById("themeIcon").className = "fas fa-moon";
      } else {
        document.getElementById("themeIcon").className = "fas fa-sun";
      }
    }

    updateThemeIcon(theme);
   

    const handleThemeToggle = () => {
      const newTheme = theme === "dark" ? "light" : "dark";
      setTheme(newTheme);
      localStorage.setItem("theme", newTheme);
    };

    const handleMobileMenuToggle = () => {
      setSidebarOpen(!sidebarOpen);
    };

    const handleDocumentClick = (e) => {
      if (window.innerWidth <= 1024) {
        const sidebar = document.getElementById("sidebar");
        const mobileMenuBtn = document.getElementById("mobileMenuBtn");
        if (
          sidebar &&
          mobileMenuBtn &&
          !sidebar.contains(e.target) &&
          !mobileMenuBtn.contains(e.target)
        ) {
          setSidebarOpen(false);
        }
      }
    };

    const handleWindowResize = () => {
      if (window.innerWidth > 1024) {
        setSidebarOpen(false);
      }
    };

    const handleNavLinkClick = (e) => {
      e.preventDefault();
      const link = e.target.closest(".nav-link");
      if (link) {
        const sectionName = link.querySelector("span").textContent.toLowerCase();
        showSection(sectionName);
      }
    };

    const showSection = (sectionName) => {
      const sections = document.querySelectorAll(".section");
      sections.forEach((section) => section.classList.remove("active"));
      let targetSection = document.getElementById(`${sectionName}-section`);
      if (targetSection) {
        targetSection.classList.add("active");
      }
    };

    const handleSearchInputFocus = () => {
      if (searchInputRef.current) {
        searchInputRef.current.parentElement.style.transform = "scale(1.02)";
      }
    };

    const handleSearchInputBlur = () => {
      if (searchInputRef.current) {
        searchInputRef.current.parentElement.style.transform = "scale(1)";
      }
    };

    const handleKeyDown = (e) => {
      if ((e.ctrlKey || e.metaKey) && e.key === "k") {
        e.preventDefault();
        searchInputRef.current.focus();
      }
    };

    const handleNotificationClick = () => {
      const notificationBtn = document.querySelector(".notification-btn");
      if (notificationBtn) {
        const badge = notificationBtn.querySelector(".notification-badge");
        if (badge) {
          badge.style.animation = "none";
          setTimeout(() => {
            badge.style.animation = "pulse 0.5s ease-in-out";
          }, 10);
        }
      }
    };

    // Event listeners setup
    document.addEventListener("click", handleDocumentClick);
    window.addEventListener("resize", handleWindowResize);

    const navLinks = document.querySelectorAll(".nav-link");
    navLinks.forEach((link) => {
      link.addEventListener("click", handleNavLinkClick);
    });

    const searchInput = document.querySelector(".search-input");
    searchInput.addEventListener("focus", handleSearchInputFocus);
    searchInput.addEventListener("blur", handleSearchInputBlur);

    document.addEventListener("keydown", handleKeyDown);

    const notificationBtn = document.querySelector(".notification-btn");
    notificationBtn.addEventListener("click", handleNotificationClick);

    const style = document.createElement("style");
    style.textContent = `
      @keyframes pulse {
        0% { transform: scale(1); }
        50% { transform: scale(1.2); }
        100% { transform: scale(1); }
      }
    `;
    document.head.appendChild(style);

    document.documentElement.style.scrollBehavior = "smooth";

    const statCards = document.querySelectorAll(".stat-card");
    statCards.forEach((card) => {
      card.addEventListener("mouseenter", () => {
        card.style.transform = "translateY(-8px) scale(1.02)";
      });
      card.addEventListener("mouseleave", () => {
        card.style.transform = "translateY(0) scale(1)";
      });
    });

    window.addEventListener("load", () => {
      document.body.style.opacity = "1";
      const cards = document.querySelectorAll(".stat-card");
      cards.forEach((card, index) => {
        setTimeout(() => {
          card.style.opacity = "1";
          card.style.transform = "translateY(0)";
        }, index * 100);
      });
    });

    document.body.style.opacity = "0";
    document.body.style.transition = "opacity 0.3s ease";
    statCards.forEach((card) => {
      card.style.opacity = "0";
      card.style.transform = "translateY(20px)";
      card.style.transition = "all 0.6s ease";
    });

    // Cleanup function
    return () => {
      document.removeEventListener("click", handleDocumentClick);
      window.removeEventListener("resize", handleWindowResize);
      navLinks.forEach((link) => {
        link.removeEventListener("click", handleNavLinkClick);
      });
      searchInput.removeEventListener("focus", handleSearchInputFocus);
      searchInput.removeEventListener("blur", handleSearchInputBlur);
      document.removeEventListener("keydown", handleKeyDown);
      notificationBtn.removeEventListener("click", handleNotificationClick);
    };
  }, [theme]);

  return (
    <div className="dashboard-container">
      {/* sidebar */}
      <aside className={`sidebar ${sidebarOpen ? "open" : ""}`} id="sidebar">
        <div className="sidebar-header">
          <div className="logo">
            <div className="logo-icon">
              <i className="fas fa-heartbeat"></i>
            </div>
            <span>MedAdmin</span>
          </div>
        </div>

        <nav>
          <ul className="nav-menu">
            <li className="nav-item">
              <a href="/" className="nav-link active">
                <i className="nav-icon fas fa-tachometer-alt"></i>
                <span>Dashboard</span>
              </a>
            </li>
            <li className="nav-item">
              <a href="/" className="nav-link">
                <i className="nav-icon fas fa-user-injured"></i>
                <span>Patient</span>
              </a>
            </li>
            <li className="nav-item">
              <a href="/" className="nav-link">
                <i className="nav-icon fas fa-user-md"></i>
                <span>Doctor</span>
              </a>
            </li>
            <li className="nav-item">
              <a href="/" className="nav-link">
                <i className="nav-icon fas fa-prescription-bottle-alt"></i>
                <span>Prescription</span>
              </a>
            </li>
            <li className="nav-item">
              <a href="/" className="nav-link">
                <i className="nav-icon fas fa-user-shield"></i>
                <span>Admin Register</span>
              </a>
            </li>
          </ul>
        </nav>
      </aside>

      {/* header */}

      <header className="header">
        <div className="header-left">
          <button className="mobile-menu-btn" onClick={handleMobileMenuToggle}>
            <i className="fas fa-bars"></i>
          </button>

          <div className="search-container">
            <i className="search-icon fas fa-search"></i>
            <input
              type="text"
              className="search-input"
              placeholder="Search or type command..."
              ref={searchInputRef}
            />
          </div>
        </div>

        <div className="header-right">
          <button
            className="theme-toggle"
            onClick={handleThemeToggle}
            title="Toggle theme"
          >
            <i className="fas fa-sun" id="themeIcon"></i>
          </button>

          <button className="notification-btn" title="Notifications">
            <i className="fas fa-bell"></i>
            <span className="notification-badge">3</span>
          </button>

          <div className="user-profile">
            <div className="user-avatar">M</div>
            <div className="user-info">
              <div className="user-name">Musharof</div>
              <div className="user-role">Admin</div>
            </div>
            <i
              className="fas fa-chevron-down"
              style={{ color: "var(--text-secondary)", fontSize: "0.8rem" }}
            ></i>
          </div>
        </div>
      </header>

      {/* main */}

      <main className="main-content">
        {/* Dashboard Section */}
        <div id="dashboard-section" className="section active">
          <div className="page-header fade-in-up">
            <nav className="breadcrumb">
              <span className="breadcrumb-item">Dashboard</span>
              <i className="breadcrumb-separator fas fa-chevron-right"></i>
              <span className="breadcrumb-item active">Overview</span>
            </nav>

            <h1 className="page-title">Dashboard Overview</h1>
            <p className="page-subtitle">
              Welcome back! Here's what's happening in your medical facility
              today.
            </p>
          </div>

          {/* Stats Cards */}
          <div className="stats-grid">
            <div
              className="stat-card slide-in-left"
              style={{ animationDelay: "0.1s" }}
            >
              <div className="stat-header">
                <div>
                  <div className="stat-title">Total Patients</div>
                  <div className="stat-value">1,243</div>
                </div>
                <div className="stat-icon patients">
                  <i className="fas fa-user-injured"></i>
                </div>
              </div>
              <div className="stat-change positive">
                <i className="change-icon fas fa-arrow-up"></i>
                <span>+5.2%</span>
                <span className="change-text">from last month</span>
              </div>
            </div>

            <div
              className="stat-card slide-in-left"
              style={{ animationDelay: "0.2s" }}
            >
              <div className="stat-header">
                <div>
                  <div className="stat-title">Total Doctors</div>
                  <div className="stat-value">89</div>
                </div>
                <div className="stat-icon doctors">
                  <i className="fas fa-user-md"></i>
                </div>
              </div>
              <div className="stat-change positive">
                <i className="change-icon fas fa-arrow-up"></i>
                <span>+2.1%</span>
                <span className="change-text">from last week</span>
              </div>
            </div>

            <div
              className="stat-card slide-in-left"
              style={{ animationDelay: "0.3s" }}
            >
              <div className="stat-header">
                <div>
                  <div className="stat-title">Total Admins</div>
                  <div className="stat-value">12</div>
                </div>
                <div className="stat-icon admins">
                  <i className="fas fa-user-shield"></i>
                </div>
              </div>
              <div className="stat-change positive">
                <i className="change-icon fas fa-arrow-up"></i>
                <span>+1</span>
                <span className="change-text">new this month</span>
              </div>
            </div>

            <div
              className="stat-card slide-in-left"
              style={{ animationDelay: "0.4s" }}
            >
              <div className="stat-header">
                <div>
                  <div className="stat-title">Total Prescriptions</div>
                  <div className="stat-value">2,847</div>
                </div>
                <div className="stat-icon prescriptions">
                  <i className="fas fa-prescription-bottle-alt"></i>
                </div>
              </div>
              <div className="stat-change positive">
                <i className="change-icon fas fa-arrow-up"></i>
                <span>+8.7%</span>
                <span className="change-text">from last month</span>
              </div>
            </div>
          </div>
        </div>

        {/* Patient Section */}
        <div id="patient-section" className="section">
