import React, { useState } from "react";
import "../styles/Login.css";
import Logo from "../assets/logo light.jpg";

function Login() {
  const [showCard2, setShowCard2] = useState(false);

  const handleLoginClick = () => {
    setShowCard2(true);
  };

  return (
    <div className="Login-Cards">
      {!showCard2 ? (
        <div className="Login-card-1 animate-slide">
          <img src={Logo} alt="" />
          <h2>Login to Medical Notes</h2>
          <p>Access patient data, prescriptions, and visit history securely.</p>
          <button className="btn btn-primary" onClick={handleLoginClick}>
            Login
          </button>
        </div>
      ) : (
        <div className="Login-card-2 animate-slide">
          <h2>Welcome to Medical Notes</h2>
          <p>This is the dashboard or next card shown after login.</p>
          <button
            className="btn btn-outline"
            onClick={() => setShowCard2(false)}
          >
            Back
          </button>
        </div>
      )}
    </div>
  );
}

export default Login;
