import React, { useState } from "react";
import "../styles/Login.css";
import Logo from "../assets/logo light.jpg";

function Login() {
  const [showCard2, setShowCard2] = useState(false);
  const [role, setRole] = useState("patient");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleLoginClick = () => {
    setShowCard2(true);
    setErrorMessage("");
  };

  const handleNextClick = async () => {
    setLoading(true);
    setErrorMessage("");

    let url = "";
    let body = {};

    if (role === "admin") {
      url = "http://localhost:8080/auth/admin/login";
      // Support numeric adminId or email or phone
      body = isNaN(username)
        ? username.includes("@")
          ? { email: username, password }
          : { phone: username, password }
        : { adminId: parseInt(username), password };
    } else if (role === "doctor") {
      url = "http://localhost:8080/auth/doctor/login";
      body = isNaN(username)
        ? username.includes("@")
          ? { email: username, password }
          : { phone: username, password }
        : { doctorId: parseInt(username), password };
    } else {
      url = "http://localhost:8080/auth/patient/login";
      body = {
        email: username,
        password,
      };
    }

    try {
      const response = await fetch(url, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(
          data.message || "Invalid credentials. Please try again."
        );
      }

      if (data.token) {
        localStorage.setItem("token", data.token);
        localStorage.setItem("role", role);
        console.log("Token received:", data.token);
        window.location.href = `/${role}/dashboard`;
      } else {
        throw new Error("Token not received.");
      }
    } catch (error) {
      setErrorMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="Login-Cards">
      <div
        className={`card-transition ${showCard2 ? "card-hide" : "card-show"}`}
      >
        <div className="Login-card-1">
          <img src={Logo} alt="" />
          <h2>Login to Medical Notes</h2>
          <p>Access patient data, prescriptions, and visit history securely.</p>
          <button className="btn btn-primary" onClick={handleLoginClick}>
            Login
          </button>
        </div>
      </div>

      <div
        className={`card-transition ${showCard2 ? "card-show" : "card-hide"}`}
      >
        <div className="login-container Login-card-2">
          <img src={Logo} alt="MedicNotes" className="logo" />
          <h2>Sign in to MedicNotes</h2>
          <p className="subtitle">Access your Account securely</p>

          <label htmlFor="role">I am a</label>
          <select
            id="role"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="patient">Patient</option>
            <option value="doctor">Doctor</option>
            <option value="admin">Admin</option>
          </select>

          <label htmlFor="email">Username / Email / Phone / ID</label>
          <input
            type="text"
            id="email"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="e.g., dr.john@example.com or 9176543210"
          />

          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Your password"
          />

          {errorMessage && <div className="error-message">{errorMessage}</div>}

          {loading && <div className="loading-message">Logging in...</div>}

          <button className="btn" onClick={handleNextClick} disabled={loading}>
            {loading ? "Please wait..." : "Next"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Login;
