import React, { useState } from "react";
import Login from "./components/Login";
import AdminDashboard from "./components/AdminDashboard";
import DoctorDashboard from "./components/DoctorDashboard";
import PatientDashboard from "./components/PatientDashboard";
import "@fortawesome/fontawesome-free/css/all.min.css";

function App() {
  const [auth, setAuth] = useState({
    isAuthenticated: false,
    role: null,
    username: "",
  });

  const renderDashboard = () => {
    switch (auth.role) {
      case "ADMIN":
        return <AdminDashboard auth={auth} setAuth={setAuth} />;

      case "DOCTOR":
        return <DoctorDashboard auth={auth} setAuth={setAuth} />;
      case "PATIENT":
        return <PatientDashboard auth={auth} setAuth={setAuth} />;
      default:
        return <div>Unknown role</div>;
    }
  };

  return (
    <>
      {!auth.isAuthenticated ? <Login setAuth={setAuth} /> : renderDashboard()}
    </>
  );
}

export default App;
