  useEffect(() => {
    // Theme Toggle Functionality
    const themeToggle = document.getElementById("themeToggle");
    const themeIcon = document.getElementById("themeIcon");
    const body = document.body;
    const currentTheme = localStorage.getItem("theme") || "dark";
    body.setAttribute("data-theme", currentTheme);
    updateThemeIcon(currentTheme);
    themeToggle.addEventListener("click", () => {
      const currentTheme = body.getAttribute("data-theme");
      const newTheme = currentTheme === "dark" ? "light" : "dark";
      body.setAttribute("data-theme", newTheme);
      localStorage.setItem("theme", newTheme);
      updateThemeIcon(newTheme);
    });
    function updateThemeIcon(theme) {
      if (theme === "dark") {
        themeIcon.className = "fas fa-moon";
      } else {
        themeIcon.className = "fas fa-sun";
      }
    }
    const mobileMenuBtn = document.getElementById("mobileMenuBtn");
    const sidebar = document.getElementById("sidebar");
    mobileMenuBtn.addEventListener("click", () => {
      sidebar.classList.toggle("open");
    });
    document.addEventListener("click", e => {
      if (window.innerWidth <= 1024) {
        if (!sidebar.contains(e.target) && !mobileMenuBtn.contains(e.target)) {
          sidebar.classList.remove("open");
        }
      }
    });
    window.addEventListener("resize", () => {
      if (window.innerWidth > 1024) {
        sidebar.classList.remove("open");
      }
    });
    const navLinks = document.querySelectorAll(".nav-link");
    const sections = document.querySelectorAll(".section");
    navLinks.forEach(link => {
      link.addEventListener("click", e => {
        e.preventDefault();
        navLinks.forEach(l => l.classList.remove("active"));
        link.classList.add("active");
        const sectionName = link.querySelector("span").textContent.toLowerCase();
        showSection(sectionName);
      });
    });
    function showSection(sectionName) {
      sections.forEach(section => section.classList.remove("active"));
      let targetSection;
      switch (sectionName) {
        case "dashboard":
          targetSection = document.getElementById("dashboard-section");
          break;
        case "patient":
          targetSection = document.getElementById("patient-section");
          break;
        case "doctor":
          targetSection = document.getElementById("doctor-section");
          break;
        default:
          targetSection = document.getElementById("dashboard-section");
      }
      if (targetSection) {
        targetSection.classList.add("active");
      }
    }
    function initTabNavigation() {
      const tabButtons = document.querySelectorAll(".tab-btn");
      tabButtons.forEach(button => {
        button.addEventListener("click", () => {
          const tabName = button.getAttribute("data-tab");
          const parentSection = button.closest(".section");
          parentSection.querySelectorAll(".tab-btn").forEach(btn => btn.classList.remove("active"));
          parentSection.querySelectorAll(".tab-content").forEach(content => content.classList.remove("active"));
          button.classList.add("active");
          const targetContent = document.getElementById(tabName);
          if (targetContent) {
            targetContent.classList.add("active");
          }
        });
      });
    }
    let patients = [{
      id: "P001",
      name: "John Smith",
      email: "john.smith@email.com",
      phone: "+1-555-0123",
      gender: "male",
      treatmentType: "cardiology",
      status: "active"
    }, {
      id: "P002",
      name: "Sarah Johnson",
      email: "sarah.j@email.com",
      phone: "+1-555-0124",
      gender: "female",
      treatmentType: "general",
      status: "active"
    }, {
      id: "P003",
      name: "Michael Brown",
      email: "michael.b@email.com",
      phone: "+1-555-0125",
      gender: "male",
      treatmentType: "orthopedics",
      status: "inactive"
    }]; // omitted for brevity
    let doctors = [{
      id: "D001",
      name: "Dr. Emily Wilson",
      email: "emily.wilson@hospital.com",
      phone: "+1-555-0201",
      specialization: "cardiology",
      experience: 12,
      status: "active"
    }, {
      id: "D002",
      name: "Dr. James Davis",
      email: "james.davis@hospital.com",
      phone: "+1-555-0202",
      specialization: "neurology",
      experience: 8,
      status: "active"
    }, {
      id: "D003",
      name: "Dr. Lisa Anderson",
      email: "lisa.anderson@hospital.com",
      phone: "+1-555-0203",
      specialization: "pediatrics",
      experience: 15,
      status: "active"
    }];
    function initFormHandling() {
      const patientForm = document.getElementById("patient-form");
      if (patientForm) {
        patientForm.addEventListener("submit", e => {
          e.preventDefault();
          const formData = new FormData(patientForm);
          const patientData = Object.fromEntries(formData);

          // Generate ID
          patientData.id = "P" + String(patients.length + 1).padStart(3, "0");
          patientData.status = "active";

          // Add to patients array
          patients.push(patientData);

          // Reset form
          patientForm.reset();

          // Show success message
          alert("Patient registered successfully!");

          // Refresh patient table if visible
          if (document.getElementById("patient-list").classList.contains("active")) {
            renderPatientTable();
          }
        });
      }

      // Doctor Form
      const doctorForm = document.getElementById("doctor-form");
      if (doctorForm) {
        doctorForm.addEventListener("submit", e => {
          e.preventDefault();
          const formData = new FormData(doctorForm);
          const doctorData = Object.fromEntries(formData);

          // Generate ID
          doctorData.id = "D" + String(doctors.length + 1).padStart(3, "0");
          doctorData.status = "active";

          // Add to doctors array
          doctors.push(doctorData);

          // Reset form
          doctorForm.reset();

          // Show success message
          alert("Doctor registered successfully!");

          // Refresh doctor table if visible
          if (document.getElementById("doctor-list").classList.contains("active")) {
            renderDoctorTable();
          }
        });
      }
    }
    function renderPatientTable(filteredPatients = patients) {
      const tbody = document.getElementById("patient-table-body");
      if (!tbody) return;
      tbody.innerHTML = "";
      if (filteredPatients.length === 0) {
        tbody.innerHTML = `
                    <tr>
                        <td colspan="8" class="empty-state">
                            <i class="fas fa-user-injured"></i>
                            <p>No patients found</p>
                        </td>
                    </tr>
                `;
        return;
      }
      filteredPatients.forEach(patient => {
        const row = document.createElement("tr");
        row.innerHTML = `
                    <td>${patient.id}</td>
                    <td>${patient.name}</td>
                    <td>${patient.email}</td>
                    <td>${patient.phone}</td>
                    <td>${patient.gender ? patient.gender.charAt(0).toUpperCase() + patient.gender.slice(1) : "N/A"}</td>
                    <td>${patient.treatmentType ? patient.treatmentType.charAt(0).toUpperCase() + patient.treatmentType.slice(1) : "N/A"}</td>
                    <td><span class="status-badge status-${patient.status}">${patient.status.charAt(0).toUpperCase() + patient.status.slice(1)}</span></td>
                    <td>
                        <div class="action-buttons">
                            <button class="btn-small btn-edit" onclick="editPatient('${patient.id}')" title="Edit">
                                <i class="fas fa-edit"></i>
                            </button>
                            <button class="btn-small btn-delete" onclick="deletePatient('${patient.id}')" title="Delete">
                                <i class="fas fa-trash"></i>
                            </button>
                        </div>
                    </td>
                `;
        tbody.appendChild(row);
      });
    }
    function renderDoctorTable(filteredDoctors = doctors) {
      const tbody = document.getElementById("doctor-table-body");
      if (!tbody) return;
      tbody.innerHTML = "";
      if (filteredDoctors.length === 0) {
        tbody.innerHTML = `
                    <tr>
                        <td colspan="8" class="empty-state">
                            <i class="fas fa-user-md"></i>
                            <p>No doctors found</p>
                        </td>
                    </tr>
                `;
        return;
      }
      filteredDoctors.forEach(doctor => {
        const row = document.createElement("tr");
        row.innerHTML = `
                    <td>${doctor.id}</td>
                    <td>${doctor.name}</td>
                    <td>${doctor.email}</td>
                    <td>${doctor.phone}</td>
                    <td>${doctor.specialization ? doctor.specialization.charAt(0).toUpperCase() + doctor.specialization.slice(1) : "N/A"}</td>
                    <td>${doctor.experience || 0} years</td>
                    <td><span class="status-badge status-${doctor.status}">${doctor.status.charAt(0).toUpperCase() + doctor.status.slice(1)}</span></td>
                    <td>
                        <div class="action-buttons">
                            <button class="btn-small btn-edit" onclick="editDoctor('${doctor.id}')" title="Edit">
                                <i class="fas fa-edit"></i>
                            </button>
                            <button class="btn-small btn-delete" onclick="deleteDoctor('${doctor.id}')" title="Delete">
                                <i class="fas fa-trash"></i>
                            </button>
                        </div>
                    </td>
                `;
        tbody.appendChild(row);
      });
    }
    function initFiltering() {
      const patientFilter = document.getElementById("patient-filter");
      const patientSearch = document.getElementById("patient-search");
      if (patientFilter) {
        patientFilter.addEventListener("change", filterPatients);
      }
      if (patientSearch) {
        patientSearch.addEventListener("input", filterPatients);
      }

      // Doctor filtering
      const doctorFilter = document.getElementById("doctor-filter");
      const doctorSearch = document.getElementById("doctor-search");
      if (doctorFilter) {
        doctorFilter.addEventListener("change", filterDoctors);
      }
      if (doctorSearch) {
        doctorSearch.addEventListener("input", filterDoctors);
      }
    }
    function filterPatients() {
      const filterValue = document.getElementById("patient-filter").value;
      const searchValue = document.getElementById("patient-search").value.toLowerCase();
      let filtered = patients;

      // Apply filter
      if (filterValue !== "all") {
        filtered = filtered.filter(patient => patient.gender === filterValue || patient.treatmentType === filterValue);
      }

      // Apply search
      if (searchValue) {
        filtered = filtered.filter(patient => patient.name.toLowerCase().includes(searchValue) || patient.email.toLowerCase().includes(searchValue) || patient.phone.includes(searchValue));
      }
      renderPatientTable(filtered);
    }
    function filterDoctors() {
      const filterValue = document.getElementById("doctor-filter").value;
      const searchValue = document.getElementById("doctor-search").value.toLowerCase();
      let filtered = doctors;

      // Apply filter
      if (filterValue !== "all") {
        filtered = filtered.filter(doctor => doctor.specialization === filterValue);
      }

      // Apply search
      if (searchValue) {
        filtered = filtered.filter(doctor => doctor.name.toLowerCase().includes(searchValue) || doctor.email.toLowerCase().includes(searchValue) || doctor.specialization.toLowerCase().includes(searchValue));
      }
      renderDoctorTable(filtered);
    }

